// Enhanced Message Processor with Better Session Management
const inputData = $input.first().json;

// Extract message details
let userMessage = '';
let userId = '';
let phone = '';
let messageType = 'text';

// Handle different input formats
if (inputData.chatInput) {
  userMessage = inputData.chatInput;
  userId = inputData.contact?.id || inputData.userId || 'anonymous';
  phone = inputData.contact?.phone || inputData.phone || '';
} else if (inputData.message) {
  userMessage = inputData.message.content || inputData.message;
  messageType = inputData.message.type || 'text';
  userId = inputData.contact?.id || 'anonymous';
  phone = inputData.contact?.phone || '';
}

// Clean and validate inputs
userMessage = String(userMessage || '').trim();
userId = String(userId || 'anonymous');
phone = String(phone || '').replace(/\D/g, ''); // Remove non-digits from phone

// Use phone as primary identifier, fallback to userId if no phone
const primaryId = phone || userId || 'anonymous';

// Generate session ID - Daily sessions (kept for backward compatibility)
const today = new Date().toISOString().split('T')[0];
const sessionId = `${primaryId}_${today}`;

// Generate unique conversation ID with timestamp for precise ordering
const now = new Date();
const timestamp = now.getTime();
const conversationId = `conv_${timestamp}_${Math.random().toString(36).substr(2, 9)}`;

// Create a message ID that includes timestamp for sorting
const messageId = `msg_${timestamp}_${primaryId}`;

// Calculate message number for today (for context)
const todayStart = new Date();
todayStart.setHours(0, 0, 0, 0);
const messageTimestamp = now.getTime();

return {
  json: {
    // Core message data
    userMessage: userMessage,
    userId: userId,
    phone: phone,
    primaryId: primaryId,
    messageType: messageType,
    sessionId: sessionId,
    conversationId: conversationId,
    messageId: messageId,

    // Timestamp data for proper ordering
    timestamp: now.toISOString(),
    timestampMillis: timestamp,
    date: today,
    time: now.toTimeString().split(' ')[0], // HH:MM:SS

    // Metadata
    hasMessage: !!userMessage,
    hasPhone: !!phone,

    // For AI Agent context with time info
    aiContext: {
      user_phone: phone,
      primary_id: primaryId,
      current_message: userMessage,
      session_id: sessionId,
      message_timestamp: now.toISOString(),
      conversation_id: conversationId,
      message_id: messageId,
      time_of_day: now.getHours() < 12 ? 'morning' : 
                   now.getHours() < 17 ? 'afternoon' : 
                   now.getHours() < 20 ? 'evening' : 'night'
    },

    // Original data for debugging
    originalData: inputData
  }
};

// Get the input data
const inputData = $input.first().json;

// Create a deep copy of the input data
const modifiedData = JSON.parse(JSON.stringify(inputData));

// Function to replace localhost URL with bot.creatiqai.com
function replaceMediaUrl(url) {
  if (url && url.includes('http://localhost:9000')) {
    return url.replace('http://localhost:9000', 'https://bot.creatiqai.com');
  }
  return url;
}

// Replace media URL if it exists
if (modifiedData.media && modifiedData.media.url) {
  modifiedData.media.url = replaceMediaUrl(modifiedData.media.url);
}

// Also add a direct access field for convenience
const mediaUrl = modifiedData.media ? modifiedData.media.url : null;
const mediaType = modifiedData.media ? modifiedData.media.type : null;

// Return the modified data with additional fields for easy access
return [
{
  ...modifiedData,
  // Add convenient access fields
  mediaUrl: mediaUrl,
  mediaType: mediaType,
  originalUrl: inputData.media ? inputData.media.url : null, // Keep original for reference
  // Add message content for easy access
  messageContent: inputData.message ? inputData.message.content : null,
  messageType: inputData.messageType || null
}
];

// Enhanced Format Output - Handle both audio transcription and direct text with language detection
const inputData = $input.first().json;
let chatInput = '';
let messageSource = '';
let phoneNumber = '';
let detectedLanguage = 'unknown';

console.log('=== Enhanced Format Output Debug ===');
console.log('Input data keys:', Object.keys(inputData));

// Simple language detection function
function detectLanguage(text) {
  if (!text) return 'unknown';

  // Check for Chinese characters
  const chineseRegex = /[\u4e00-\u9fff\u3400-\u4dbf\uf900-\ufaff]/;
  if (chineseRegex.test(text)) {
    return 'chinese';
  }

  // Check for Malay/Indonesian common words
  const malayWords = ['saya', 'anda', 'dengan', 'untuk', 'adalah', 'yang', 'ini', 'itu', 'pada', 'dari'];
  const lowerText = text.toLowerCase();
  if (malayWords.some(word => lowerText.includes(word))) {
    return 'malay';
  }

  // Default to English if no specific patterns found
  return 'english';
}

// Determine the source and extract appropriate data
if (inputData.text && typeof inputData.text === 'string') {
  // This is from OpenAI5 (audio transcription)
  chatInput = inputData.text.trim();
  messageSource = 'audio_transcribed';
  detectedLanguage = detectLanguage(chatInput);

  console.log('Source: Audio transcription');
  console.log('Transcribed text:', chatInput);
  console.log('Detected language:', detectedLanguage);

  // For audio messages, get contact info from the original trigger
  try {
    const triggerData = $('When chat message received').first().json;
    phoneNumber = triggerData.contact?.phone || triggerData.phone || '';
    console.log('Phone from trigger (audio):', phoneNumber);
  } catch (e) {
    console.log('Could not access trigger data for audio:', e.message);
    phoneNumber = '';
  }

} else if (inputData.chatInput) {
  // This is from Code5 (direct text message)
  chatInput = inputData.chatInput;
  messageSource = inputData.messageSource || 'text';
  phoneNumber = inputData.phone || '';
  detectedLanguage = detectLanguage(chatInput);

  console.log('Source: Direct text from Code5');
  console.log('Detected language:', detectedLanguage);

} else {
  // Fallback extraction
  console.log('Using fallback extraction');

  if (inputData.message?.content) {
    chatInput = inputData.message.content;
  } else if (inputData.contact?.message) {
    chatInput = inputData.contact.message;
  } else if (typeof inputData === 'string') {
    chatInput = inputData;
  } else {
    try {
      const triggerData = $('When chat message received').first().json;
      chatInput = triggerData.chatInput || triggerData.message?.content || 'Hello';
      phoneNumber = triggerData.contact?.phone || triggerData.phone || '';
      console.log('Using trigger data as fallback');
    } catch (e) {
      console.log('Could not access trigger data:', e.message);
      chatInput = 'Hello';
    }
  }

  messageSource = 'fallback';
  detectedLanguage = detectLanguage(chatInput);
}

// Clean up the message
chatInput = String(chatInput || 'Hello').trim();

// If we still don't have phone number, try to get it from trigger
if (!phoneNumber) {
  try {
    const triggerData = $('When chat message received').first().json;
    phoneNumber = triggerData.contact?.phone || triggerData.phone || '';
    console.log('Phone from trigger (fallback):', phoneNumber);
  } catch (e) {
    console.log('Could not get phone from trigger:', e.message);
    phoneNumber = '';
  }
}

// Generate contact info
const contact = {
  id: phoneNumber ? phoneNumber.replace(/\D/g, '') : 'anonymous',
  phone: phoneNumber
};

console.log('=== Enhanced Format Output Results ===');
console.log('Final chatInput:', chatInput);
console.log('Message source:', messageSource);
console.log('Phone number:', phoneNumber);
console.log('Detected language:', detectedLanguage);
console.log('Has text:', !!chatInput);

// Return standardized format with language information
return {
  json: {
    chatInput: chatInput,
    contact: contact,
    phone: phoneNumber,
    messageType: messageSource,
    detectedLanguage: detectedLanguage,
    isMediaMessage: messageSource === 'audio_transcribed',
    sessionId: `conv_${Date.now()}_${contact.id}`,
    timestamp: new Date().toISOString(),

    // Language context for the AI to maintain consistency
    languageContext: {
      inputLanguage: detectedLanguage,
      shouldReplyInSameLanguage: true,
      isAudioTranscription: messageSource === 'audio_transcribed'
    },

    // Debug information
    debug: {
      source: messageSource,
      hasText: !!chatInput,
      hasPhone: !!phoneNumber,
      detectedLanguage: detectedLanguage,
      inputKeys: Object.keys(inputData),
      processingComplete: true
    }
  }
};

// Simplified Conversation Summarizer - Include current message in history
const allItems = $input.all();

// Get current message and phone from Message Processor
let userPhone = '';
let currentMessage = '';
try {
  const currentMessageData = $('Message Processor').first().json;
  userPhone = currentMessageData.phone || currentMessageData.primaryId || '';
  currentMessage = currentMessageData.userMessage || '';
} catch (e) {
  userPhone = '';
  currentMessage = '';
}

// Extract all past conversation data
let conversationHistoryArray = [];
allItems.forEach((item) => {
  const data = item.json;
  if (data.conversation_content && data.created_at) {
    conversationHistoryArray.push(data);
  }
});

// Sort past conversations by timestamp (oldest first)
const sortedPastConversations = conversationHistoryArray
  .sort((a, b) => new Date(a.created_at) - new Date(b.created_at))
  .map((item, index) => ({
    text: item.conversation_content,
    timestamp: item.created_at,
    order: index + 1
  }));

// Add current message as the latest conversation
const allConversations = [...sortedPastConversations];
if (currentMessage) {
  allConversations.push({
    text: `user: ${currentMessage}`,
    timestamp: new Date().toISOString(),
    order: sortedPastConversations.length + 1
  });
}

// Return clean output with current message included
return {
  json: {
    phone: userPhone,
    conversationHistory: allConversations,
    totalConversations: allConversations.length,
    currentMessage: currentMessage
  }
};

// Final Response - Return only clean text without URLs
const data = $('Format Conversation for Storage').first().json;

// Get the clean text message (URLs already removed)
let message = data.text_message || '';

// Return clean output
return {
  json: {
    output: message.trim()
  }
};

